import React from 'react';
import Box from '@mui/material/Box';
import Stat from '../Stat/Stat';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import Select, { SelectChangeEvent } from '@mui/material/Select';
const Jouster = () => {

  const [strength, setStrength] = React.useState(5);
  const [luck, setLuck] = React.useState(5);
  const [dexterity, setDexterity] = React.useState(5);
  const [name, setName] = React.useState('Harry Potter');

  return (

    <Box
      sx={{
        width: 300,
        height: 260,
        backgroundColor: 'primary.dark',
        '&:hover': {
          backgroundColor: 'primary.main',
          opacity: [0.9, 0.8, 0.7],
        },
        '& .MuiTextField-root': { m: 1, width: '30ch' },
      }}
    >

      <TextField
        required
        id="outlined-required"
        label="Name"
        defaultValue={name}
        onChange={(event) => {
          setName(event.target.value);
        }}
        InputLabelProps={{
          shrink: true,
        }}
        variant="filled"
      />
      <Select
    labelId="demo-simple-select-label"
    id="demo-simple-select"
    value={age}
    label="Age"
    onChange={handleChange}
  >
    <MenuItem value={10}>Ten</MenuItem>
    <MenuItem value={20}>Twenty</MenuItem>
    <MenuItem value={30}>Thirty</MenuItem>
  </Select>
      <Stat statName ="Strength" defaultVal={strength} minVal={1} maxVal={10} onChange={(event) => {
          setStrength(event.target.value);
        }}>
      </Stat>
      <Stat statName ="Dexterity" defaultVal={dexterity} minVal={1} maxVal={10} onChange={(event) => {
          setDexterity(event.target.value);
        }}>
      </Stat>
      <Stat statName ="Luck" defaultVal={luck} minVal={1} maxVal={10} onChange={(event) => {
          setLuck(event.target.value);
        }}>
      </Stat>
      {luck}
    </Box>

  )
};

Jouster.propTypes = {};

Jouster.defaultProps = {};

export default Jouster;
