import React from 'react';
import Box from '@mui/material/Box';
import Stat from '../Stat/Stat';
import TextField from '@mui/material/TextField';
const Jouster = () => {

  const [strength, setStrength] = React.useState(5);
  const [luck, setLuck] = React.useState(5);
  const [dexterity, setDexterity] = React.useState(5);
  const [name, setName] = React.useState('Harry Potter');

  return (

    <Box
      sx={{
        width: 300,
        height: 300,
        backgroundColor: 'primary.dark',
        '&:hover': {
          backgroundColor: 'primary.main',
          opacity: [0.9, 0.8, 0.7],
        },
      }}
    >
      <TextField
        required
        id="outlined-required"
        label="Name"
        defaultValue={name}
        onChange={(event) => {
          setName(event.target.value);
        }}
      />
      <Stat statName ="Strength" defaultVal={strength} minVal={1} maxVal={10}></Stat>
      <Stat statName ="Dexterity" defaultVal={dexterity} minVal={1} maxVal={10}></Stat>
      <Stat statName ="Luck" defaultVal={luck} minVal={1} maxVal={10}></Stat>
    </Box>

  )
};

Jouster.propTypes = {};

Jouster.defaultProps = {};

export default Jouster;
