import React from 'react';
import PropTypes from 'prop-types';
import Jouster from '../Jouster/Jouster';




const WizardJousting = () => {
  const [jouster1, setJouster1] = React.useState({health : 10, strength : 5, luck : 5, house : 'Gryffindor', name : 'Harry Potter'});
  const [jouster2, setJouster2] = React.useState({health : 10, strength : 5, luck : 5, house : 'Gryffindor', name : 'Harry Potter'});

  const setJouster = (playerOne, newStr, newLuck, newHouse, newName, newHealth = null) => {
    if(playerOne === true){
      setJouster1({health : newHealth == null ? jouster1.health : newHealth, strength : newStr, luck : newLuck, house : newHouse, name : newName})
    }
    else{
      setJouster2({health : newHealth == null ? jouster1.health : newHealth, strength : newStr, luck : newLuck, house : newHouse, name : newName})

    }
  }
  return (
  <div>
    <Jouster setJoustPlayer={setJouster} playerOne={true}></Jouster>
  </div>
  )
};

WizardJousting.propTypes = {};

WizardJousting.defaultProps = {};

export default WizardJousting;
